<?phpadd_action('admin_init', 'wpla_register_settings');function wpla_register_settings() {	register_setting('login_alerts_settings_group', 'login_alerts_settings');}/* * Option panel on WP Dashboard */function wpla_admin(){	global $login_alerts_options;		ob_start(); ?>	<div class="wrap">	<div id="icon-options-general" class="icon32">	<br>	</div>	<h2>WP Login Alert to im.kayac.com Options</h2>	<form method="post" action="options.php">			<?php settings_fields('login_alerts_settings_group'); ?>			<p>Push notification to your Windows, Mac, Tablet PC and smartphones via <a href="http://im.kayac.com" target=_blank>im.kayac.com</a> service if detected someone trying to your WordPress login panel. Please input username of your account and secret key of your API.</p><p>Your registered username</p>		<label class="description" for="login_alerts_settings[username]">Username (required)</label>		<input type="text" id="login_alerts_settings[username]" name="login_alerts_settings[username]" value="<?php echo $login_alerts_options['username']; ?>" />		<p>Strongly recommend that you choose "Use secret key authentication" on API Authentication from Setting panel.</p>		<label class="description" for="login_alerts_settings[secretkey]">Secret key (if you need)</label>		<input type="text" id="login_alerts_settings[secretkey]" name="login_alerts_settings[secretkey]" value="<?php echo $login_alerts_options['secretkey']; ?>" />		<p class="submit">			<input type="submit" class="button-primary" value="Save Settings" />		</p>	</form>		</div><!-- .wrap -->	<?php	echo ob_get_clean();}